import axiosClient from "./axiosClient";

const EmployeeApi  = {
    getAll : (params) => {
    const url = '/employees';
    return axiosClient.get(url, { params });
    },
    createEmployee : (employee) => {
        const url = '/employees';
        return axiosClient.post(url, employee);
        }
    }
    export default EmployeeApi;
